memberdef.initializer:
do {                                                                                       \
    OSMutex* __next = mutex->link.next;                                                    \
    if (__next == NULL)                                                                    \
    {                                                                                      \
        (queue)->tail = 0;                                                                 \
    }                                                                                      \
    else                                                                                   \
    {                                                                                      \
        __next->link.prev = 0;                                                             \
    }                                                                                      \
    (queue)->head = __next;                                                                \
}                                                                                          \
while (0);
